import { createContext, createSignal, useContext } from "solid-js";

const ${1:${TM_FILENAME_BASE/(.*?)\Context.*/$1/i}}Context = createContext();

export function ${1:${TM_FILENAME_BASE/(.*?)\Context.*/$1/i}}Provider(props) {
	const [${TM_FILENAME_BASE/(.*?)\Context.*/${1:/downcase}/i}, set${TM_FILENAME_BASE/(.*?)\Context.*/${1:/capitalize}/i}] = createSignal(props.${TM_FILENAME_BASE/(.*?)\Context.*/${1:/downcase}/i} || ""),
		store = [${TM_FILENAME_BASE/(.*?)\Context.*/${1:/downcase}/i}, set${TM_FILENAME_BASE/(.*?)\Context.*/${1:/capitalize}/i}];

	return (
		<${1:${TM_FILENAME_BASE/(.*?)\Context.*/$1/i}}Context.Provider value={store}>{props.children}</${1:${TM_FILENAME_BASE/(.*?)\Context.*/$1/i}}Context.Provider>
	);
}

export function use${TM_FILENAME_BASE/(.*?)\Context.*/${1:/capitalize}/i}() {
	return useContext(${1:${TM_FILENAME_BASE/(.*?)\Context.*/$1/i}}Context);
}
